mapper.select:
select (@i:=@i+1) xh, sr.id, sr.area_no, sr.area_name, sr.depart_id, sr.depart_name, sr.manage_user, sr.manage_user_name, sr.manage_phone, sr.item_ids, sr.desc, sr.images, bsc.name status, sr.record_status, sr.record_user_no, sr.record_user_name, sr.record_user_phone, sr.record_location, sr.record_location_name, sr.app_client_id, DATE_FORMAT(sr.create_time,'%Y-%m-%d') create_time, DATE_FORMAT(sr.update_time,'%Y-%m-%d') update_time from scan_records sr,(select @i:=0)t,back_status_config bsc where 1=1 and (sr.record_status = bsc.flag)
and area_no = #{area_no}
and depart_id = #{depart_id}
and record_status = #{status}
and ( area_name like concat('%',#{search},'%') or depart_name like concat('%',#{search},'%') or manage_user like concat('%',#{search},'%') or manage_user_name like concat('%',#{search},'%') or manage_user_name like concat('%',#{search},'%') or manage_phone like concat('%',#{search},'%') or record_user_name like concat('%',#{search},'%') or record_location_name like concat('%',#{search},'%') )
and create_time >= #{start_time}
and create_time <= #{end_time}
order by create_time desc
